const onlyType = compositionTypeArrey.map((el) => el.type);
export type compositionType = typeof onlyType[number];
const compositionStatusArrey = [
  "preliminary",
  "final",
  "amended",
  "entered-in-error",
] as const;
type compositionStatus = typeof compositionStatusArrey[number];

export interface compositionAuthor {
  reference: string;
  display: string;
}

export interface COMPOSITOIN {
  id?: string;
  identifier?: string;
  /**
   * user Object
   */
  user?: USER_COMPOSITION_EXTENSION[];
  patient: PATIENT;
  patientId: string;
  encounter: ENCOUNTER;
  encounterId: string;
  date: string;
  author: compositionAuthor[];
  organization: ORGANIZATION;
  organizationId: string;
  status: compositionStatus;
  type: compositionType;
  documentDatahtml?: string;
  section: any[];
  extension?: EXTENSION[];
  documentSubType?:string
}

export interface USER_COMPOSITION_EXTENSION {
  date: string;
  id: number;
  orgId: number;
  name: string;
  orgName: string;
}
export class Composition extends ResourceMain implements ResourceMaster {
  async toHtml(composition:COMPOSITOIN):Promise<string> {
    return composition.documentDatahtml || ""
    
  }
  private compType!: {
    type: string;
    system: "https://ndhm.gov.in/sct" | "http://snomed.info/sct";
    url: string;
    code: string;
    text: string;
  };
  public mapCompositionType(type: compositionType) {
    this.compType = compositionTypeArrey.filter((comp) => comp.type == type)[0];
  }

  private _patient!: PATIENT;
  public get patient(): PATIENT {
    return this._patient;
  }

  private _organization!: ORGANIZATION;
  public get organization(): ORGANIZATION {
    return this._organization;
  }
  /**
   * This are persons who interpt the result in care of diagnostic report and in care of the others these who treat patinets
   */
  private _practitioner: any = [];
  public get practitioner(): any {
    return this._practitioner;
  }

  private _encounter!: ENCOUNTER;
  public get encounter(): ENCOUNTER {
    return this._encounter;
  }
  /**
   * This is for diagnostic reporting enity requesting the services
   */
  private requeter: string = "";
  /** This form specimen */
  private specimenCollectedTime = "";
   /** This form specimen */
  private specimenRecivedTime ="";
  /**Sample Type */
  private specimenType =""

  private reportedDateandTime ="";

  private performer: string[] = [];

  async setEncounter(id: string) {
    let curClass = new Encounter();
    const res = await new GcpFhirCRUD().getFhirResource(id, "Encounter");
    this._encounter = curClass.convertFhirToObject(res.data);
  }

  async setPatient(id: string) {
    let curClass = new Patient();
    const res = await new GcpFhirCRUD().getFhirResource(id, "Patient");
    this._patient = curClass.convertFhirToObject(res.data);
  }
  async setOrganization(id: string) {
    let curClass = new Organization();
    const res = await new GcpFhirCRUD().getFhirResource(id, "Organization");
    this._organization = curClass.convertFhirToObject(res.data);
  }
  async setPractitioner(id: string) {
    let curClass = new Practitioner();
    const res = await new GcpFhirCRUD().getFhirResource(id, "Practitioner");

    this._practitioner.push({
      reference: `Practitioner/${res.data.id}}`,
      display: curClass.convertFhirToObject(res.data).name,
    });
  }



  /**
   * service requested by could be patient , organization, patient him or herself
   * this is applicable diagnostic report
   */
  setRequester = (options: {
    reesource: resourceType;
    display: string;
  }): void => {
    if (options.reesource == "Patient") {
      this.requeter = "Self";
    } else {
      this.requeter = `${options.display}`;
    }
  };


  setSpecimenRecivedAndCollectedTime = (specimen:SPECIMEN)=>{
    this.specimenCollectedTime=specimen.collection && specimen.collection.collectedDateTime;
    this.specimenRecivedTime = specimen.recivedDateTime;
    this.specimenType=specimen.type.text || "";

  }

  setDiagnosticReportDateTime=(diagnosticReport:DIAGNOSTIC_REPORT)=>{
    this.reportedDateandTime= new TimeZone().convertTZ(diagnosticReport.issuedDate, "Asia/Kolkata", false);
  }

  setPerformer = (options: {
    reesource: resourceType;
    display: string;
  }): void => {
    this.performer.push(options.display);
  };
  getFHIR(options: COMPOSITOIN) {
    const getpatientdetails = () => {
      let ret = `<div>Patient:- ${options.patient.name}.</div>`;
      ret += `<div>UHID :- ${options.patient.MRN} </div>`;
      ret += `${
        options.patient.phrAddress
          ? `<div>ABHA Address : ${options.patient.phrAddress}. ${
              options.patient.healthNumber
                ? `ABHA Number ${options.patient.healthNumber}`
                : ""
            }</div>`
          : ""
      }`;
      ret += `<div>Gender/Age: ${options.patient.gender}/${new Age().dobToAge(
        new Date(options.patient.dob)
      )} ph: ${options.patient.mobile}</div>`;

      return ret.trim();
    };
    /**
     * This is for doctors who authored the document or who interpreted the results
     * @returns
     */
    const getDoctors = () => {
      let str: string = "";
      options.author.forEach((el, i) => {
        if (i > 0) {
          str = str + `<div>${el.display}</div>`;
        } else {
          str = `<div><b>Signed By :- ${el.display}</b></div>`;
        }
      });
      return str;
    };

    const getHtmlText = (): string => {
      let html = `<div xmlns="http://www.w3.org/1999/xhtml">`;
      html += `<div style="text-align: right">`;
      html += `Date:-${new TimeZone().convertTZ(
        options.date,
        process.env.TZ as any,
        false
      )}`;
      html += `</div>`;
      html += `<div style="text-align: right; font-size: 9px">`;
      html += `Docurment Status :${options.status}`;
      html += `</div>`;
      html += `<div style="text-align: right; font-size: 9px">`;
      html += `Docurment Type :${options.type}`;
      html += `</div>`;
      html += `<table data-pdfmake="{'widths':['60%','40%']}">`;
      html += `<tr>`;
      html += `<td>${getpatientdetails()}</td>`;
      html += `<td>${getDoctors()}`;

      html += `${
        this.performer.length > 0
          ? `<div>Performed By :${this.performer.reduce(
              (pr, cu) => (pr += `${cu}<\br>`)
            )}</div>`
          : ""
      }`;
      html += `</td>`;
      html += `</tr>`;

      html += `${
        this.requeter || options.patient.internalId || options.patient.facilityId
          ? `<tr><td>${
              this.requeter ? `Requested By : ${this.requeter}` : ""
            }</td><td>${
              options.patient.internalId || options.patient.facilityId
                ? `Internal Id : ${options.patient.facilityId || ""} ${options.patient.internalId || ""}`
                : ""
            }</td></tr>`
          : ""
      }`;

      html += `${this.specimenCollectedTime || this.specimenRecivedTime || this.specimenType ? 
        `<tr><td>
          ${
            this.specimenCollectedTime && `Specimen Collection Time ${new TimeZone().convertTZ(this.specimenCollectedTime, "Asia/Kolkata", false)}<br/>`
          }
          ${
            this.specimenRecivedTime && `Specimen Received Time ${new TimeZone().convertTZ(this.specimenRecivedTime, "Asia/Kolkata", false)}`
          }
        
        </td><td>
          ${this.reportedDateandTime && `Report Date ${this.reportedDateandTime}<br/>`}
          ${this.specimenType && `Specimen Type ${this.specimenType}`}
        
        </td></tr>`
        :
        ""
      }`

      html += `</table>`;
      html += `<div>${options.documentDatahtml}</div`;
      html += `</div>`;
      // html = `<span style="font-size: 10px;">${html}</span>`

      return html.trim();
    };

    const extensions: any[] = [];
    if (options.user && options.user.length > 0) {
      options.user.forEach((el) => {
        extensions.push({
          url: "https://www.nicehms.com/user",
          valueString: JSON.stringify(el),
        });
      });
    }

    

    if(options.extension && options.extension.length >0){
      options.extension.forEach(el=>
        extensions.push(el)
      )
    }

    if(options.documentSubType){
      if(options.extension && options.extension.length >0){
        const index = options.extension.findIndex(el => el.url == "https://www.nicehms.com/documentSubType");
        if (index <0){
          extensions.push({
            "url" : "https://www.nicehms.com/documentSubType",
            "valueString" : options.documentSubType
          })
        }
     }else{

      extensions.push({
        "url" : "https://www.nicehms.com/documentSubType",
        "valueString" : options.documentSubType
      })
     }
    }

    this.mapCompositionType(options.type);
    const body = {
      resourceType: "Composition",
      id: options.id || undefined,
      meta: {
        versionId: "1",
        lastUpdated: new Date().toISOString(),
        profile: [this.compType.url],
      },
      language: "en-IN",
      text: {
        status: "generated",
        div: getHtmlText(),
      },
      extension: extensions,
      identifier: {
        system: "https://ndhm.in/phr",
        value: options.identifier || uuidv4(),
      },
      status: options.status,
      type: {
        coding: [
          {
            system: "https://ndhm.gov.in/sct",
            code: this.compType.code,
            display: this.compType.text,
          },
        ],
        text: this.compType.text,
      },
      subject: {
        reference: `Patient/${options.patientId}`,
        // "display": options.patient.name
      },
      encounter: {
        reference: `Encounter/${options.encounterId}`,
      },
      date: options.date,
      author: options.author,
      title: options.type,
      custodian: {
        reference: `Organization/${options.organizationId}`,
        // "display": options.organization.name
      },
      section: [
        {
          title: this.compType.type,
          code: {
            coding: [
              {
                system: "https://ndhm.gov.in/sct",
                code: this.compType.code,
                display: options.type,
              },
            ],
          },
          entry: options.section,
        },
      ],
    };

    return body;
  }
  convertFhirToObject(options: any): Partial<COMPOSITOIN> {
    let ret: Partial<COMPOSITOIN> = {
      patient: undefined,
      patientId: this.getIdFromReference({
        ref: options.subject.reference,
        resourceType: "Patient",
      }),
      encounter: undefined,
      encounterId: this.getIdFromReference({
        ref: options.encounter.reference,
        resourceType: "Encounter",
      }),
      date: options.date,
      organization: undefined,
      // organizationId: `${options.custodian.reference}`.substring(13),
      organizationId: this.getIdFromReference({
        ref: options.custodian.reference,
        resourceType: "Organization",
      }),
      status: options.status,
      type: options.title,
      section: options.section,
      id: options.id,
      identifier: options.identifier.value,
      author: options.author,
      documentDatahtml: options.text.div.trim(),
    };
    if (ret.patient == undefined) {
      delete ret.patient;
    }
    if (ret.encounter == undefined) {
      delete ret.encounter;
    }
    if (ret.organization == undefined) {
      delete ret.organization;
    }
    if (options.extension) {
      const user: any[] = options.extension
        .filter((el: any) => {
          if ((el.url == "https://www.nicehms.com/user")) {
            return el;
          }
        })
        .map((pl: any) => {
          return JSON.parse(pl.valueString);
        });

      ret.user = user;
      ret.extension =options.extension
    }

    return ret;
  }

  statusArray(): compositionStatus[] {
    return compositionStatusArrey.map((el) => el);
  }

  typeArray(): compositionType[] {
    return onlyType.map((el) => el);
  }

  /**
   * this function return the all resources of copistion namely Patient, Author/Practioner, Encounter and entries in section
   * @param id composition id
   * @returns
   */
  getWithIncludes = async (id: string) => {
    const gcpFhirSearch = new GcpFhirSearch();
    const res = await gcpFhirSearch.search(
      "Composition",
      `_id=${id}&_include=Composition:patient&_include=Composition:author&_include=Composition:author&_include=Composition:encounter&_include=Composition:entry`
    );
    return res;
  };

  /**
   * This function returns all the compositions of patient
   * @param id of patient
   * @returns
   */
  getCompositionsByPatient = async (id: string): Promise<any> => {
    const gcpFhirSearch = new GcpFhirSearch();
    const res = await gcpFhirSearch.search(
      "Patient",
      `_id=${id}&_revinclude=Composition:patient`
    );
    return res;
  };

/**
 * 
 * @param id This array of ids many merged pateints
 * @returns compostions
 */
  getCompositionsByPatientArray= async(id:string[]):Promise<any>=>{
    const gcpFhirSearch = new GcpFhirSearch();
    const res = await gcpFhirSearch.search("Composition", `subject=${id.map(el=> el).join(",")}`)
    return res
  }

  getPdf = async (options: {
    html: string;
    header?: (options: PDF_HEADER) => [];
    footer?: (options: PDF_FOOter) => [];
    composition: COMPOSITOIN;
    base64: boolean;
    signBase64: string;
    nameLine1: string;
    nameLine2?: string;
    qrCode: string;
    qrCodeWidth?: number;
    paperSize: string;
    headerbase64Image?: string;
    paragraphSpace?: number;
    singleImagePerPage?: boolean;

    /**
     * This is letter pad header preprinted
     */
    topMargin?: number;
    /*
     * this is for setting footer height in care of preprinted paper
     */
    bottomMargin?: number;
  }): Promise<string | Buffer> => {
    // code check media

    const mediaId: string[] = options.composition.section[0].entry.filter((el:any)=>{
      if(el.type=="Media"){
        return el
      }
    }).map(
      (el: any) => {
        const id = this.getIdFromReference({
          ref: el.reference,
          resourceType: "Media",
        });
        return id;
      }
    );

    const mediaContent: string[] = [];
    if(mediaId.length > 0){
      await this.getMediaComposition(0, mediaId, mediaContent);
    }
   

    const pdf = new CreatePdf();
    const retPdf = await pdf.create(options.html, {
      paragraphSpace: options.paragraphSpace || 6,
      base64: options.base64,
      header: options.header,
      footer: options.footer,
      topMargin: options.topMargin,
      bottomMargin: options.bottomMargin,
      esign: options.signBase64
        ? {
            image:
              options.composition.status == "final"
                ? options.signBase64
                : emptySign,
            nameLine1: options.nameLine1,
            nameLine2: options.nameLine2 || "",
          }
        : undefined,
      qrcode: options.qrCode || undefined,
      qrCodeWidth: options.qrCodeWidth || undefined,
      paperSize: options.paperSize,
      headerbase64Image: options.headerbase64Image,
      media:
        mediaContent.length > 0
          ? {
              content: mediaContent,
              singleImagePerPage: options.singleImagePerPage || false,
            }
          : undefined,
    });

    return retPdf;
  };

  getMediaComposition = async (
    index: number,
    mediaId: string[],
    content: string[]
  ) => {
    if (index >= mediaId.length) {
      return;
    }
    const resource = (
      await new GcpFhirCRUD().getFhirResource(mediaId[index], "Media")
    ).data;
    content.push(
      `data:image/jpeg;base64,${
        new Media().convertFhirToObject(resource).base64Data
      }`
    );
    index = index + 1;
    await this.getMediaComposition(index, mediaId, content);
  };

  /**
   * This returns the diagnosis from encounter
   * @param encounter
   * @returns
   */
  async getDiagnosisFromEnconter(
    diagnosis: ENCOUNTER["diagnosis"],
    index: number,
    diagnosisStringArray: string[]
  ) {
    if (!diagnosis || index >= diagnosis.length) {
      return;
    }

    const resource = this.getFromMultResource({
      reference: diagnosis[index].condition.reference,
    });

    const condition = (
      await new GcpFhirCRUD().getFhirResource(resource.id, resource.resource)
    ).data;
    diagnosisStringArray.push(condition.text.div);
    index++;
    await this.getDiagnosisFromEnconter(diagnosis, index, diagnosisStringArray);
  }
}

export const emptySign = `data:image/gif;base64,`;

export interface Records {
  create: Function;
  update: Function;
  getOptions: Function;
}
